function inkthemes_add_stylesheet() {
    wp_enqueue_style('coloroptions', get_template_directory_uri() . "/color/" . inkthemes_get_option('inkthemes_altstylesheet') . ".css", '', '', 'all');
}

add_action('init', 'inkthemes_add_stylesheet');
